var concat = function(values, separator, prefix, suffix, func) {
    var result = "";
    if(values != null && values instanceof Array) {
        for(var i=0; i<values.length; i++) {
            if(i>0) {
                result += separator;
            }
            var value = values[i];
            if(func != null) {
                value = func(value);
            }
            result += prefix + value + suffix;
        }
    }
    return result;
}

module.exports=concat;