// each
module.exports = function(values, func) {
    if(values == null) {
        return;
    }
    if(values instanceof Object) {
        if(values instanceof Array) {
            var length = values.length;
            for(var i=0; i<length; i++) {
                func(values[i]);
            }
        }
        else {
            for(var valueKey in values) {
                func(values[valueKey], valueKey);
            }
        }
    }
}