var has = require("./has");

var filter = function(values, keyName, keyValue) {
    if(values instanceof Array) {
        var results = [];
        for(var i=0; i<values.length; i++) {
            if(has(values[i],keyName,keyValue)) {
                results.push(values[i]);
            }
        }
        return results;
    }
}

module.exports = filter;