php.floor = function (value) {
    // Returns the next lowest integer value from the number  
    // 
    // version: 1008.1718
    // discuss at: http://phpjs.org/functions/floor
    // +   original by: Onno Marsman
    // *     example 1: \php.floor(8723321.4);
    // *     returns 1: 8723321
    
    return Math.floor(value);
};

