php.tan = function (arg) {
    // Returns the tangent of the number in radians  
    // 
    // version: 1008.1718
    // discuss at: http://phpjs.org/functions/tan
    // +   original by: Onno Marsman
    // *     example 1: \php.tan(8723321.4);
    // *     returns 1: 5.4251848798444815
    return Math.tan(arg);
};

